import { $$ } from 'uikit/src/js/util/dom';
import { isVisible } from 'uikit/src/js/util/filter.js';

export function getMaxPathLength(el) {
    return isVisible(el)
        ? Math.ceil(
              Math.max(0, ...$$('[stroke]', el).map((stroke) => stroke.getTotalLength?.() || 0)),
          )
        : 0;
}
